import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { EmployeeService } from '../../services/employee.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-employee-form',
  templateUrl: './employee-form.component.html'
})
export class EmployeeFormComponent implements OnInit {
  form: FormGroup;
  id?: number;

  constructor(private fb: FormBuilder, private service: EmployeeService, private route: ActivatedRoute, private router: Router) {
    this.form = this.fb.group({
      name: ['', Validators.required],
      email: ['',[Validators.email]],
      phone: [''],
      department: [''],
      designation: ['']
    });
  }

  ngOnInit(): void {
    this.route.params.subscribe(p => {
      if (p['id']) {
        this.id = +p['id'];
        this.service.get(this.id).subscribe(e => this.form.patchValue(e));
      }
    });
  }

  save() {
    if (this.form.invalid) return;

    const emp = this.form.value;
    if (this.id) {
      this.service.update(this.id, emp).subscribe(() => this.router.navigate(['/employees']));
    } else {
      this.service.create(emp).subscribe(() => this.router.navigate(['/employees']));
    }
  }
}
<h3 *ngIf="!id">Add Employee</h3>
<h3 *ngIf="id">Edit Employee</h3>

<form [formGroup]="form" (ngSubmit)="save()">
  <div class="mb-3">
    <label class="form-label">Name</label>
    <input class="form-control" formControlName="name" />
  </div>
  <div class="mb-3">
    <label class="form-label">Email</label>
    <input class="form-control" formControlName="email" />
  </div>
  <div class="mb-3">
    <label class="form-label">Phone</label>
    <input class="form-control" formControlName="phone" />
  </div>
  <div class="mb-3">
    <label class="form-label">Department</label>
    <input class="form-control" formControlName="department" />
  </div>
  <div class="mb-3">
    <label class="form-label">Designation</label>
    <input class="form-control" formControlName="designation" />
  </div>
  <button class="btn btn-primary" type="submit">Save</button>
  <a class="btn btn-secondary ms-2" routerLink="/employees">Cancel</a>
</form>
