import { Component } from '@angular/core';
import { Router } from '@angular/router';

interface Booking {
  id: number;
  photographerName: string;
  eventType: string;
  date: Date;
  location: string;
  price: number;
  status: string;
}

@Component({
  selector: 'app-bookings',
  templateUrl: './bookings.component.html',
  styleUrls: ['./bookings.component.css']
})
export class BookingsComponent {
  bookings: Booking[] = [
    {
      id: 1,
      photographerName: 'Wedding Specialist',
      eventType: 'Wedding',
      date: new Date('2026-03-15'),
      location: 'New York',
      price: 25000,
      status: 'Confirmed'
    },
    {
      id: 2,
      photographerName: 'Portrait Expert',
      eventType: 'Portrait',
      date: new Date('2026-02-20'),
      location: 'Los Angeles',
      price: 8000,
      status: 'Pending'
    }
  ];

  constructor(private router: Router) { }

  goToPhotographers() {
    this.router.navigate(['/photographers']);
  }

  cancelBooking(id: number) {
    if (confirm('Are you sure you want to cancel this booking?')) {
      this.bookings = this.bookings.filter(b => b.id !== id);
    }
  }
}
<div class="container">
  <h1>My Bookings</h1>

  <div *ngIf="bookings.length === 0" class="no-bookings">
    <i class="fa-solid fa-calendar-x"></i>
    <h2>No bookings yet</h2>
    <p>You haven't booked any photographers yet.</p>
    <button class="btn-primary" (click)="goToPhotographers()">Browse Photographers</button>
  </div>

  <div *ngIf="bookings.length > 0" class="bookings-list">
    <div class="booking-card" *ngFor="let booking of bookings">
      <div class="booking-header">
        <h3>{{ booking.photographerName }}</h3>
        <span class="badge" [ngClass]="'status-' + booking.status.toLowerCase()">{{ booking.status }}</span>
      </div>
      <div class="booking-details">
        <p><b>Event Type:</b> {{ booking.eventType }}</p>
        <p><b>Date:</b> {{ booking.date | date: 'MMM dd, yyyy' }}</p>
        <p><b>Location:</b> {{ booking.location }}</p>
        <p><b>Price:</b> ₹{{ booking.price | number }}</p>
      </div>
      <div class="booking-actions">
        <button class="btn-secondary" *ngIf="booking.status === 'Confirmed'">Reschedule</button>
        <button class="btn-danger" (click)="cancelBooking(booking.id)">Cancel</button>
      </div>
    </div>
  </div>
</div>

<footer>© 2026 Photographer Booking Platform | PhotoBook</footer>
