import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-navbar',
  templateUrl: './navbar.component.html',
  styleUrls: ['./navbar.component.css']
})
export class NavbarComponent {
  currentRoute: string = 'home';

  constructor(private router: Router) {
    this.router.events.subscribe(() => {
      const url = this.router.url;
      if (url === '/' || url === '') this.currentRoute = 'home';
      else if (url.includes('photographers')) this.currentRoute = 'photographers';
      else if (url.includes('bookings')) this.currentRoute = 'bookings';
      else if (url.includes('dashboard')) this.currentRoute = 'dashboard';
      else if (url.includes('login')) this.currentRoute = 'login';
    });
  }

  navigate(route: string) {
    this.currentRoute = route;
  }
}
<nav>
  <h2><i class="fa-solid fa-camera"></i> PhotoBook</h2>
  <ul>
    <li routerLink="/" (click)="navigate('home')" [class.active]="currentRoute === 'home'">Home</li>
    <li routerLink="/photographers" (click)="navigate('photographers')" [class.active]="currentRoute === 'photographers'">Photographers</li>
    <li routerLink="/bookings" (click)="navigate('bookings')" [class.active]="currentRoute === 'bookings'">Bookings</li>
    <li routerLink="/dashboard" (click)="navigate('dashboard')" [class.active]="currentRoute === 'dashboard'">Dashboard</li>
    <li routerLink="/login" (click)="navigate('login')" [class.active]="currentRoute === 'login'">Login</li>
  </ul>
</nav>
