import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { EmployeeService } from '../../services/employee.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-employee-form',
  templateUrl: './employee-form.component.html'
})
export class EmployeeFormComponent implements OnInit {
  form: FormGroup;
  id?: number;

  constructor(private fb: FormBuilder, private service: EmployeeService, private route: ActivatedRoute, private router: Router) {
    this.form = this.fb.group({
      name: ['', Validators.required],
      email: ['',[Validators.email]],
      phone: [''],
      department: [''],
      designation: ['']
    });
  }

  ngOnInit(): void {
    this.route.params.subscribe(p => {
      if (p['id']) {
        this.id = +p['id'];
        this.service.get(this.id).subscribe(e => this.form.patchValue(e));
      }
    });
  }

  save() {
    if (this.form.invalid) return;

    const emp = this.form.value;
    if (this.id) {
      this.service.update(this.id, emp).subscribe(() => this.router.navigate(['/employees']));
    } else {
      this.service.create(emp).subscribe(() => this.router.navigate(['/employees']));
    }
  }
}
<div class="form-container">
  <div class="form-card">
    <h1 *ngIf="!id"><i class="fa-solid fa-user-plus"></i> Add Employee</h1>
    <h1 *ngIf="id"><i class="fa-solid fa-pen"></i> Edit Employee</h1>

    <form [formGroup]="form" (ngSubmit)="save()">
      <div class="form-group">
        <label for="name">Name</label>
        <input 
          id="name"
          class="form-control" 
          formControlName="name"
          placeholder="Full Name" 
        />
      </div>

      <div class="form-group">
        <label for="email">Email</label>
        <input 
          id="email"
          class="form-control" 
          formControlName="email"
          type="email"
          placeholder="email@example.com" 
        />
      </div>

      <div class="form-group">
        <label for="phone">Phone</label>
        <input 
          id="phone"
          class="form-control" 
          formControlName="phone"
          placeholder="+1 (555) 000-0000" 
        />
      </div>

      <div class="form-group">
        <label for="department">Department</label>
        <input 
          id="department"
          class="form-control" 
          formControlName="department"
          placeholder="e.g., Photography, Marketing" 
        />
      </div>

      <div class="form-group">
        <label for="designation">Designation</label>
        <input 
          id="designation"
          class="form-control" 
          formControlName="designation"
          placeholder="e.g., Senior Photographer, Manager" 
        />
      </div>

      <div class="form-actions">
        <button class="btn btn-primary" type="submit">
          <i class="fa-solid fa-save"></i> Save Employee
        </button>
        <a class="btn btn-secondary" routerLink="/employees">
          <i class="fa-solid fa-times"></i> Cancel
        </a>
      </div>
    </form>
  </div>
</div>

<footer>© 2026 Photographer Booking Platform | PhotoBook</footer>
