import { Component } from '@angular/core';

interface Photographer {
  name: string;
  type: string;
  location: string;
  price: number;
  cover: string;
  works: string[];
}

@Component({
  selector: 'app-photographers',
  templateUrl: './photographers.component.html',
  styleUrls: ['./photographers.component.css']
})
export class PhotographersComponent {
  photographers: Photographer[] = [
    {
      name: 'Wedding Specialist',
      type: 'Wedding',
      location: 'New York',
      price: 25000,
      cover: 'https://images.unsplash.com/photo-1519741497674-611481863552',
      works: [
        'https://images.unsplash.com/photo-1529626455594-4ff0802cfb7e',
        'https://images.unsplash.com/photo-1519741497674-611481863552',
        'https://images.unsplash.com/photo-1520857014576-2c4f4c972b57'
      ]
    },
    {
      name: 'Portrait Expert',
      type: 'Portrait',
      location: 'Los Angeles',
      price: 8000,
      cover: 'https://images.unsplash.com/photo-1508214751196-bcfd4ca60f91',
      works: [
        'https://images.unsplash.com/photo-1508214751196-bcfd4ca60f91',
        'https://images.unsplash.com/photo-1494790108377-be9c29b29330',
        'https://images.unsplash.com/photo-1488426862026-3ee34a7d66df'
      ]
    },
    {
      name: 'Event Pro',
      type: 'Event',
      location: 'Chicago',
      price: 15000,
      cover: 'https://images.unsplash.com/photo-1526170375885-4d8ecf77b99f',
      works: [
        'https://images.unsplash.com/photo-1526170375885-4d8ecf77b99f',
        'https://images.unsplash.com/photo-1500530855697-b586d89ba3ee',
        'https://images.unsplash.com/photo-1515169067865-5387ec356754'
      ]
    }
  ];

  filteredPhotographers: Photographer[] = this.photographers;
  selectedPhotographer: Photographer | null = null;
  showNotification: boolean = false;

  selectedLocation: string = '';
  selectedBudget: string = '';
  selectedEvent: string = '';

  applyFilters() {
    this.filteredPhotographers = this.photographers.filter(p => {
      let ok = true;

      if (this.selectedLocation) {
        ok = ok && p.location === this.selectedLocation;
      }

      if (this.selectedEvent) {
        ok = ok && p.type === this.selectedEvent;
      }

      if (this.selectedBudget) {
        const [min, max] = this.selectedBudget.split('-').map(Number);
        ok = ok && p.price >= min && p.price <= max;
      }

      return ok;
    });
  }

  openProfile(photographer: Photographer) {
    this.selectedPhotographer = photographer;
  }

  closeProfile(event?: MouseEvent) {
    if (event && event.target !== event.currentTarget) return;
    this.selectedPhotographer = null;
  }

  book() {
    this.selectedPhotographer = null;
    this.showNotification = true;
    setTimeout(() => {
      this.showNotification = false;
    }, 3000);
  }
}
<section class="hero">
  <div>
    <h1>Find & Book Professional Photographers</h1>
    <p>Filter by Location, Budget & Event</p>
  </div>
</section>

<div class="container">
  <!-- FILTERS -->
  <div class="filters">
    <select [(ngModel)]="selectedLocation" (change)="applyFilters()">
      <option value="">All Locations</option>
      <option value="New York">New York</option>
      <option value="Los Angeles">Los Angeles</option>
      <option value="Chicago">Chicago</option>
    </select>

    <select [(ngModel)]="selectedBudget" (change)="applyFilters()">
      <option value="">All Budgets</option>
      <option value="5000-10000">₹5k - ₹10k</option>
      <option value="10001-20000">₹10k - ₹20k</option>
      <option value="20001-30000">₹20k - ₹30k</option>
    </select>

    <select [(ngModel)]="selectedEvent" (change)="applyFilters()">
      <option value="">All Events</option>
      <option value="Wedding">Wedding</option>
      <option value="Portrait">Portrait</option>
      <option value="Event">Event</option>
    </select>
  </div>

  <!-- PHOTOGRAPHER LIST -->
  <div class="photographers">
    <div class="card" *ngFor="let photographer of filteredPhotographers">
      <img [src]="photographer.cover" alt="{{ photographer.name }}">
      <div class="card-body">
        <h3>{{ photographer.name }}</h3>
        <p>{{ photographer.type }} | ₹{{ photographer.price | number }}</p>
        <button class="view-btn" (click)="openProfile(photographer)">View Profile</button>
      </div>
    </div>
  </div>

  <div *ngIf="filteredPhotographers.length === 0" class="no-results">
    <p>No photographers match your filters.</p>
  </div>
</div>

<!-- PROFILE MODAL -->
<div class="modal" [style.display]="selectedPhotographer ? 'flex' : 'none'" (click)="closeProfile($event)">
  <div class="modal-content" (click)="$event.stopPropagation()">
    <span class="close-btn" (click)="closeProfile()">&times;</span>
    <h2>{{ selectedPhotographer?.name }}</h2>
    <p><b>Specialization:</b> <span>{{ selectedPhotographer?.type }}</span></p>
    <p><b>Location:</b> <span>{{ selectedPhotographer?.location }}</span></p>
    <p><b>Price:</b> <span>₹{{ selectedPhotographer?.price | number }}</span></p>

    <h3 style="margin-top: 15px">Portfolio</h3>
    <div class="gallery">
      <img *ngFor="let work of selectedPhotographer?.works" [src]="work" alt="Portfolio">
    </div>

    <button class="book-btn" (click)="book()">Book Photographer</button>
  </div>
</div>

<!-- NOTIFICATION -->
<div class="notification" [style.display]="showNotification ? 'block' : 'none'">
  ✅ Booking request sent to photographer
</div>

