import { Component, OnInit } from '@angular/core';
import { EmployeeService, Employee } from '../services/employee.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-employees',
  templateUrl: './employees.component.html'
})
export class EmployeesComponent implements OnInit {
  employees: Employee[] = [];

  constructor(private service: EmployeeService, private router: Router) { }

  ngOnInit(): void {
    this.load();
  }

  load() {
    this.service.getAll().subscribe({
      next: data => {
        console.log('Employees load response:', data);
        this.employees = data;
      },
      error: err => {
        console.error('Failed to load employees', err);
        this.employees = [];
      }
    });
  }

  view(id?: number) {
    if (id) this.router.navigate(['/employees/view', id]);
  }

  edit(id?: number) {
    if (id) this.router.navigate(['/employees/edit', id]);
  }

  delete(id?: number) {
    if (!id) return;
    if (!confirm('Are you sure?')) return;
    this.service.delete(id).subscribe(() => this.load());
  }
}
<div class="d-flex justify-content-between align-items-center mb-3">
  <h3>Employees</h3>
  <button class="btn btn-primary" routerLink="/employees/add">Add Employee</button>
</div>

<table class="table table-bordered">
  <thead>
    <tr>
      <th>Name</th>
      <th>Email</th>
      <th>Phone</th>
      <th>Department</th>
      <th>Designation</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let e of employees">
      <td>{{e.name}}</td>
      <td>{{e.email}}</td>
      <td>{{e.phone}}</td>
      <td>{{e.department}}</td>
      <td>{{e.designation}}</td>
      <td>
        <button class="btn btn-sm btn-info me-1" (click)="view(e.id)">View</button>
        <button class="btn btn-sm btn-warning me-1" (click)="edit(e.id)">Edit</button>
        <button class="btn btn-sm btn-danger" (click)="delete(e.id)">Delete</button>
      </td>
    </tr>
  </tbody>
</table>
