import { Component, OnInit } from '@angular/core';
import { EmployeeService, Employee } from '../../services/employee.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-employee-view',
  templateUrl: './employee-view.component.html'
})
export class EmployeeViewComponent implements OnInit {
  employee?: Employee;

  constructor(private service: EmployeeService, private route: ActivatedRoute, private router: Router) { }

  ngOnInit(): void {
    this.route.params.subscribe(p => {
      const id = +p['id'];
      if (id) this.service.get(id).subscribe(e => this.employee = e);
    });
  }
}
<div *ngIf="employee">
  <h3>View Employee</h3>
  <dl class="row">
    <dt class="col-sm-3">Name</dt>
    <dd class="col-sm-9">{{employee.name}}</dd>

    <dt class="col-sm-3">Email</dt>
    <dd class="col-sm-9">{{employee.email}}</dd>

    <dt class="col-sm-3">Phone</dt>
    <dd class="col-sm-9">{{employee.phone}}</dd>

    <dt class="col-sm-3">Department</dt>
    <dd class="col-sm-9">{{employee.department}}</dd>

    <dt class="col-sm-3">Designation</dt>
    <dd class="col-sm-9">{{employee.designation}}</dd>
  </dl>
  <a class="btn btn-secondary" routerLink="/employees">Back</a>
</div>
